ing // Cat√©gorie (ex: \"Tarifs & Limites\", \"Fonctionnalit√©s\")\n  label    String // Label de la ligne (ex: \"Stockage\", \"API & Webhooks\")\n  order    Int     @default(0) // Ordre d'affichage\n  isActive Boolean @default(true)\n\n  // Relations\n  values PlanComparisonValue[]\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@map(\"comparison_rows\")\n}\n\n// Valeurs de comparaison pour chaque plan\nmodel PlanComparisonValue {\n  id              String        @id @default(cuid())\n  planId          String\n  plan            Plan          @relation(fields: [planId], references: [id], onDelete: Cascade)\n  comparisonRowId String\n  comparisonRow   ComparisonRow @relation(fields: [comparisonRowId], references: [id], onDelete: Cascade)\n  value           String // Valeur (ex: \"5 GB\", \"‚úÖ\", \"‚ùå\", \"Illimit√©\")\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@unique([planId, comparisonRowId])\n  @@map(\"plan_comparison_values\")\n}\n\n// Abonnement √âcole\nmodel Subscription {\n  id       String @id @default(cuid())\n  schoolId String @unique\n  school   School @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  planId   String\n  plan     Plan   @relation(fields: [planId], references: [id])\n\n  status               SubscriptionStatus\n  stripeCustomerId     String?\n  stripeSubscriptionId String?\n\n  currentPeriodStart DateTime\n  currentPeriodEnd   DateTime\n  trialEndsAt        DateTime?\n  canceledAt         DateTime?\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@map(\"subscriptions\")\n}\n\n// ============================================\n// SYST√àME DE SIGNALEMENT (SUPER-ADMIN)\n// ============================================\n\n// Signalement de Probl√®me\nmodel IssueReport {\n  id            String @id @default(cuid())\n  schoolId      String\n  school        School @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  reportedBy    String // userId\n  reporterName  String\n  reporterEmail String\n\n  title       String\n  description String\n  priority    IssuePriority\n  status      IssueStatus   @default(OPEN)\n  category    IssueCategory\n\n  resolvedBy String? // Super-Admin userId\n  resolvedAt DateTime?\n  resolution String?\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@map(\"issue_reports\")\n}\n\n// ============================================\n// ANNONCES GLOBALES\n// ============================================\n\nmodel Announcement {\n  id         String   @id @default(cuid())\n  schoolId   String? // Null si annonce Super Admin (toutes √©coles)\n  school     School?  @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  authorId   String\n  authorName String\n  authorRole UserRole\n\n  title          String\n  content        String\n  priority       AnnouncementPriority @default(NORMAL)\n  targetAudience Json // [\"TEACHER\", \"STUDENT\", \"PARENT\"] ou [\"ALL\"] - JSON pour compatibilit√© MySQL\n\n  isActive    Boolean   @default(true)\n  publishedAt DateTime  @default(now())\n  expiresAt   DateTime?\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@map(\"announcements\")\n}\n\n// ============================================\n// ENUMS\n// ============================================\n\nenum UserRole {\n  SUPER_ADMIN // Administrateur de la plateforme SAAS\n  SCHOOL_ADMIN // Administrateur d'une √©cole sp√©cifique\n  MANAGER // Manager avec permissions personnalis√©es\n  PERSONNEL // Personnel avec permissions personnalis√©es\n  ASSISTANT // Assistant avec permissions personnalis√©es\n  SECRETARY // Secr√©taire avec permissions personnalis√©es\n  TEACHER // Enseignant\n  STUDENT // √âtudiant\n  PARENT // Parent d'√©tudiant\n}\n\nenum PaymentStatus {\n  PENDING // En attente\n  PAID // Pay√©\n  OVERDUE // En retard\n  PARTIAL // Paiement partiel\n  CANCELED // Annul√©\n}\n\nenum FeeType {\n  TUITION // Frais de scolarit√©\n  REGISTRATION // Frais d'inscription\n  EXAM // Frais d'examen\n  LIBRARY // Frais de biblioth√®que\n  SPORT // Frais sportifs\n  TRANSPORT // Frais de transport\n  OTHER // Autres frais\n}\n\nenum ScholarshipType {\n  MERIT // Bourse au m√©rite\n  NEED_BASED // Bourse sociale\n  DISCOUNT // R√©duction\n  FULL // Bourse compl√®te\n  PARTIAL // Bourse partielle\n}\n\nenum SubscriptionStatus {\n  TRIAL // P√©riode d'essai\n  ACTIVE // Actif\n  PAST_DUE // Paiement en retard\n  CANCELED // Annul√©\n  UNPAID // Non pay√©\n}\n\nenum IssuePriority {\n  LOW\n  MEDIUM\n  HIGH\n  CRITICAL\n}\n\nenum IssueStatus {\n  OPEN\n  IN_PROGRESS\n  RESOLVED\n  CLOSED\n}\n\nenum IssueCategory {\n  TECHNICAL // Probl√®me technique\n  BILLING // Probl√®me de facturation\n  FEATURE_REQUEST // Demande de fonctionnalit√©\n  BUG // Bug\n  SUPPORT // Support g√©n√©ral\n  OTHER // Autre\n}\n\nenum TypeEnseignant {\n  PERMANENT\n  VACATAIRE\n  ADMINISTRATEUR\n}\n\nenum Grade {\n  PROFESSEUR\n  MAITRE_CONFERENCE\n  MAITRE_ASSISTANT\n  ASSISTANT\n}\n\nenum SchoolType {\n  UNIVERSITY // Universit√© (gestion par salles)\n  HIGH_SCHOOL // Lyc√©e (gestion par classes)\n}\n\nenum AnnouncementPriority {\n  LOW\n  NORMAL\n  HIGH\n  URGENT\n}\n\n// ============================================\n// ENUMS VIE SCOLAIRE & AGENDA\n// ============================================\n\nenum IncidentType {\n  RETARD // Retard en cours\n  RETARD_NON_JUSTIFIE // Retard non justifi√©\n  OUBLI_MATERIEL // Oubli de mat√©riel\n  COMPORTEMENT // Probl√®me de comportement\n  EXCLUSION // Exclusion de cours\n  AUTRE // Autre incident\n}\n\nenum EventType {\n  CONSEIL_CLASSE // Conseil de classe\n  REUNION_PARENTS // R√©union parents-professeurs\n  JOUR_FERIE // Jour f√©ri√©\n  VACANCES // Vacances scolaires\n  EXAMEN // Examen/DS\n  EVENEMENT_SPORTIF // √âv√©nement sportif\n  SORTIE_SCOLAIRE // Sortie scolaire\n  CONFERENCE // Conf√©rence\n  AUTRE // Autre √©v√©nement\n}\n\nenum AppointmentStatus {\n  PENDING // En attente de confirmation\n  CONFIRMED // Confirm√©\n  CANCELLED // Annul√©\n  COMPLETED // Termin√©\n}\n\n// ============================================\n// GESTION DES SALLES ET CLASSES\n// ============================================\n\n// Mod√®le Salle (pour Universit√©s)\nmodel Room {\n  id          String   @id @default(cuid())\n  name        String // \"Amphi A\", \"Salle 201\", \"Lab 1\"\n  code        String // \"A01\", \"S201\", \"L1\"\n  capacity    Int // Capacit√© en nombre d'√©tudiants\n  type        RoomType // AMPHITHEATER, CLASSROOM, LABORATORY, etc.\n  building    String? // B√¢timent\n  floor       String? // √âtage\n  equipment   String   @default(\"[]\") // JSON array: [\"Projecteur\", \"Tableau\", \"Ordinateurs\"]\n  isAvailable Boolean  @default(true)\n  schoolId    String\n  school      School   @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@unique([code, schoolId])\n  @@map(\"rooms\")\n}\n\n// Mod√®le Classe (pour Lyc√©es)\nmodel Class {\n  id       String  @id @default(cuid())\n  name     String // \"Terminale S1\", \"Premi√®re A\", \"Seconde B\"\n  code     String // \"TS1\", \"1A\", \"2B\"\n  niveau   String // \"Terminale\", \"Premi√®re\", \"Seconde\"\n  capacity Int // Capacit√© en nombre d'√©l√®ves\n  mainRoom String? // Salle principale assign√©e\n  schoolId String\n  school   School  @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@unique([code, schoolId])\n  @@map(\"classes\")\n}\n\n// ============================================\n// BETTER AUTH MODELS\n// ============================================\n\nmodel Session {\n  id        String   @id\n  expiresAt DateTime\n  token     String   @unique\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n  ipAddress String?\n  userAgent String?\n  userId    String\n  user      User     @relation(fields: [userId], references: [id], onDelete: Cascade)\n\n  @@map(\"session\")\n}\n\nmodel Account {\n  id                    String    @id\n  accountId             String\n  providerId            String\n  userId                String\n  user                  User      @relation(fields: [userId], references: [id], onDelete: Cascade)\n  accessToken           String?\n  refreshToken          String?\n  idToken               String?\n  accessTokenExpiresAt  DateTime?\n  refreshTokenExpiresAt DateTime?\n  scope                 String?\n  password              String?\n  createdAt             DateTime  @default(now())\n  updatedAt             DateTime  @updatedAt\n\n  @@map(\"account\")\n}\n\nmodel Verification {\n  id         String   @id\n  identifier String\n  value      String\n  expiresAt  DateTime\n  createdAt  DateTime @default(now())\n  updatedAt  DateTime @updatedAt\n\n  @@map(\"verification\")\n}\n\nenum RoomType {\n  AMPHITHEATER // Amphith√©√¢tre\n  CLASSROOM // Salle de classe\n  LABORATORY // Laboratoire\n  COMPUTER_LAB // Salle informatique\n  LIBRARY // Biblioth√®que\n  SPORTS_HALL // Salle de sport\n  CONFERENCE // Salle de conf√©rence\n  OTHER // Autre\n}\n\n// ============================================\n// DOCUMENTS & RESSOURCES\n// ============================================\n\n// Document (pour partage de ressources p√©dagogiques)\nmodel Document {\n  id          String           @id @default(cuid())\n  title       String\n  description String?\n  fileName    String\n  fileUrl     String\n  fileSize    Int // Taille en bytes\n  mimeType    String\n  category    DocumentCategory\n  schoolId    String\n  school      School           @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  moduleId    String?\n  module      Module?          @relation(fields: [moduleId], references: [id])\n  uploadedBy  String // userId\n  isPublic    Boolean          @default(false) // Visible par tous ou seulement enseignants\n  createdAt   DateTime         @default(now())\n  updatedAt   DateTime         @updatedAt\n\n  @@map(\"documents\")\n}\n\nenum DocumentCategory {\n  COURSE // Cours\n  EXERCISE // Exercice\n  EXAM // Examen\n  CORRECTION // Correction\n  PRESENTATION // Pr√©sentation\n  VIDEO // Vid√©o\n  OTHER // Autre\n}\n\n// ============================================\n// PERMISSIONS SYSTEM\n// ============================================\n\n// Permission (d√©finit les permissions disponibles)\nmodel Permission {\n  id              String           @id @default(cuid())\n  name            String           @unique // Ex: \"students.view\", \"students.create\"\n  description     String // Description lisible\n  category        String // Ex: \"students\", \"teachers\", \"finance\"\n  userPermissions UserPermission[]\n  createdAt       DateTime         @default(now())\n  updatedAt       DateTime         @updatedAt\n\n  @@map(\"permissions\")\n}\n\n// UserPermission (associe les permissions aux utilisateurs)\nmodel UserPermission {\n  id           String     @id @default(cuid())\n  userId       String\n  user         User       @relation(fields: [userId], references: [id], onDelete: Cascade)\n  permissionId String\n  permission   Permission @relation(fields: [permissionId], references: [id], onDelete: Cascade)\n  canView      Boolean    @default(false)\n  canCreate    Boolean    @default(false)\n  canEdit      Boolean    @default(false)\n  canDelete    Boolean    @default(false)\n  createdAt    DateTime   @default(now())\n  updatedAt    DateTime   @updatedAt\n\n  @@unique([userId, permissionId])\n  @@map(\"user_permissions\")\n}\n\n// ============================================\n// MESSAGING SYSTEM\n// ============================================\n\n// Conversation (fil de discussion entre utilisateurs)\nmodel Conversation {\n  id       String           @id @default(cuid())\n  schoolId String\n  subject  String? // Sujet de la conversation\n  type     ConversationType @default(DIRECT) // DIRECT, GROUP, ANNOUNCEMENT\n\n  // Relations\n  participants ConversationParticipant[]\n  messages     Message[]\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@map(\"conversations\")\n}\n\n// Participant √† une conversation\nmodel ConversationParticipant {\n  id             String       @id @default(cuid())\n  conversationId String\n  conversation   Conversation @relation(fields: [conversationId], references: [id], onDelete: Cascade)\n  userId         String\n\n  // M√©tadonn√©es\n  lastReadAt DateTime? // Derni√®re lecture\n  isArchived Boolean   @default(false)\n  isMuted    Boolean   @default(false)\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@unique([conversationId, userId])\n  @@map(\"conversation_participants\")\n}\n\n// Message dans une conversation\nmodel Message {\n  id             String       @id @default(cuid())\n  conversationId String\n  conversation   Conversation @relation(fields: [conversationId], references: [id], onDelete: Cascade)\n  senderId       String // userId de l'exp√©diteur\n  senderName     String // Nom de l'exp√©diteur (d√©normalis√© pour performance)\n  senderRole     UserRole // R√¥le de l'exp√©diteur\n\n  // Contenu\n  content     String @db.Text\n  attachments String @default(\"[]\") // JSON array: [{name, url, size, type}]\n\n  // M√©tadonn√©es\n  isRead    Boolean @default(false)\n  readBy    String  @default(\"[]\") // JSON array: [userId1, userId2, ...]\n  isEdited  Boolean @default(false)\n  isDeleted Boolean @default(false)\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@index([conversationId])\n  @@index([senderId])\n  @@map(\"messages\")\n}\n\n// ============================================\n// NOTIFICATION SYSTEM\n// ============================================\n\n// Notification (syst√®me de notifications push)\nmodel Notification {\n  id       String  @id @default(cuid())\n  userId   String // Destinataire\n  schoolId String? // √âcole concern√©e (optionnel pour SUPER_ADMIN)\n\n  // Contenu\n  title    String\n  message  String               @db.Text\n  type     NotificationType\n  category NotificationCategory\n\n  // M√©tadonn√©es\n  isRead Boolean   @default(false)\n  readAt DateTime?\n\n  // Lien d'action (optionnel)\n  actionUrl   String?\n  actionLabel String?\n\n  // Donn√©es additionnelles (JSON)\n  metadata String @default(\"{}\") // JSON: {studentId, paymentId, etc.}\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@index([userId])\n  @@index([schoolId])\n  @@map(\"notifications\")\n}\n\n// ============================================\n// ENUMS FOR MESSAGING & NOTIFICATIONS\n// ============================================\n\nenum ConversationType {\n  DIRECT // Conversation 1-√†-1\n  GROUP // Conversation de groupe\n  ANNOUNCEMENT // Annonce (admin ‚Üí tous)\n}\n\nenum NotificationType {\n  INFO // Information g√©n√©rale\n  SUCCESS // Succ√®s\n  WARNING // Avertissement\n  ERROR // Erreur\n  REMINDER // Rappel\n}\n\nenum NotificationCategory {\n  MESSAGE // Nouveau message\n  PAYMENT // Paiement\n  GRADE // Note\n  ABSENCE // Absence\n  HOMEWORK // Devoir\n  ANNOUNCEMENT // Annonce\n  SYSTEM // Syst√®me\n  OTHER // Autre\n}\n\n// ============================================\n// ATTENDANCE (Pr√©sences)\n// ============================================\n\nmodel Attendance {\n  id        String           @id @default(cuid())\n  studentId String\n  moduleId  String\n  teacherId String\n  date      DateTime\n  status    AttendanceStatus\n  notes     String?          @db.Text\n  createdAt DateTime         @default(now())\n  updatedAt DateTime         @updatedAt\n\n  student Student    @relation(fields: [studentId], references: [id], onDelete: Cascade)\n  module  Module     @relation(fields: [moduleId], references: [id], onDelete: Cascade)\n  teacher Enseignant @relation(fields: [teacherId], references: [id], onDelete: Cascade)\n\n  @@index([studentId])\n  @@index([moduleId])\n  @@index([teacherId])\n  @@index([date])\n  @@map(\"attendances\")\n}\n\nenum AttendanceStatus {\n  PRESENT\n  ABSENT\n  LATE\n  EXCUSED\n}\n\n// ============================================\n// WORK GROUPS (Groupes de Travail)\n// ============================================\n\nmodel WorkGroup {\n  id       String @id @default(cuid())\n  name     String\n  schoolId String\n  school   School @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n\n  // Contexte du groupe\n  moduleId  String? // Pour universit√©\n  module    Module?  @relation(fields: [moduleId], references: [id], onDelete: Cascade)\n  filiereId String? // Pour universit√©\n  filiere   Filiere? @relation(fields: [filiereId], references: [id])\n\n  // Cr√©ateur du groupe\n  createdBy   String // userId (peut √™tre √©tudiant ou enseignant)\n  creatorRole String // STUDENT, TEACHER\n\n  // Membres\n  members WorkGroupMember[]\n\n  // Devoirs assign√©s √† ce groupe\n  homework Homework[]\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@index([schoolId])\n  @@index([moduleId])\n  @@index([filiereId])\n  @@map(\"work_groups\")\n}\n\nmodel WorkGroupMember {\n  id        String    @id @default(cuid())\n  groupId   String\n  group     WorkGroup @relation(fields: [groupId], references: [id], onDelete: Cascade)\n  studentId String\n  student   Student   @relation(fields: [studentId], references: [id], onDelete: Cascade)\n  role      String    @default(\"MEMBER\") // LEADER, MEMBER\n  joinedAt  DateTime  @default(now())\n\n  @@unique([groupId, studentId])\n  @@index([groupId])\n  @@index([studentId])\n  @@map(\"work_group_members\")\n}\n\n// ============================================\n// PERMISSIONS D'UPLOAD PERSONNALIS√âES\n// ============================================\n\nmodel UserUploadPermission {\n  id               String    @id @default(cuid())\n  userId           String\n  user             User      @relation(fields: [userId], references: [id], onDelete: Cascade)\n  grantedBy        String // userId qui a accord√© les permissions\n  grantedAt        DateTime  @default(now())\n  customCategories Json // Cat√©gories suppl√©mentaires autoris√©es - JSON pour compatibilit√© MySQL\n  customMaxSizes   Json? // Tailles max personnalis√©es par cat√©gorie\n  expiresAt        DateTime?\n  reason           String?\n  isActive         Boolean   @default(true)\n  createdAt        DateTime  @default(now())\n  updatedAt        DateTime  @updatedAt\n\n  @@index([userId])\n  @@index([grantedBy])\n  @@map(\"user_upload_permissions\")\n}\n\n// ============================================\n// SYST√àME DE NOTATION CONFIGURABLE\n// ============================================\n\n// P√©riodes de notation (Trimestres ou Semestres)\nmodel GradingPeriod {\n  id        String     @id @default(cuid())\n  schoolId  String\n  school    School     @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  name      String // \"Trimestre 1\", \"Semestre 1\", etc.\n  startDate DateTime\n  endDate   DateTime\n  isActive  Boolean    @default(true)\n  createdAt DateTime   @default(now())\n  updatedAt DateTime   @updatedAt\n  bulletins Bulletin[]\n\n  @@index([schoolId])\n  @@map(\"grading_periods\")\n}\n\n// Types d'√©valuations personnalisables par √©cole\nmodel EvaluationType {\n  id        String   @id @default(cuid())\n  schoolId  String\n  school    School   @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  name      String // \"Devoir\", \"Examen\", \"Projet\", \"TP\", etc.\n  category  String // \"HOMEWORK\" ou \"EXAM\" pour le calcul\n  weight    Float    @default(1.0) // Poids dans le calcul (ex: 2.0 pour devoirs)\n  isActive  Boolean  @default(true)\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  @@index([schoolId])\n  @@map(\"evaluation_types\")\n}\n\n// Templates de re√ßu personnalisables\nmodel ReceiptTemplate {\n  id           String   @id @default(cuid())\n  schoolId     String\n  school       School   @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  name         String // \"Re√ßu Standard\", \"Re√ßu avec Logo\", etc.\n  logoUrl      String? // URL du logo de l'√©cole\n  headerText   String? // Texte d'en-t√™te personnalis√©\n  footerText   String? // Texte de pied de page personnalis√©\n  showLogo     Boolean  @default(true)\n  showStamp    Boolean  @default(false)\n  stampUrl     String? // URL du tampon/cachet\n  primaryColor String   @default(\"#4F46E5\")\n  isActive     Boolean  @default(false) // Un seul template actif √† la fois\n  createdAt    DateTime @default(now())\n  updatedAt    DateTime @updatedAt\n\n  @@index([schoolId])\n  @@index([schoolId, isActive])\n  @@map(\"receipt_templates\")\n}\n\n// Templates PDF personnalisables (bulletins, certificats, etc.)\nmodel PDFTemplate {\n  id       String @id @default(cuid())\n  schoolId String\n  school   School @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n\n  name String @default(\"Bulletin standard\")\n\n  // Configuration en-t√™te\n  showLogo       Boolean @default(true)\n  logoPosition   String  @default(\"left\") // \"left\", \"center\", \"right\"\n  headerColor    String  @default(\"#4F46E5\")\n  schoolNameSize Int     @default(24)\n\n  // Informations √† afficher\n  showAddress Boolean @default(true)\n  showPhone   Boolean @default(true)\n  showEmail   Boolean @default(true)\n  showStamp   Boolean @default(true)\n\n  // Configuration tableau\n  gradeTableStyle String @default(\"detailed\") // \"simple\" ou \"detailed\"\n\n  // Pied de page\n  footerText     String  @default(\"Ce document est officiel et certifi√© conforme.\")\n  showSignatures Boolean @default(true)\n\n  isActive Boolean @default(false)\n\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n\n  bulletins Bulletin[]\n\n  @@unique([schoolId])\n  @@index([schoolId])\n  @@index([schoolId, isActive])\n  @@map(\"pdf_templates\")\n}\n\n// Bulletins g√©n√©r√©s (snapshots)\nmodel Bulletin {\n  id              String        @id @default(cuid())\n  schoolId        String\n  school          School        @relation(fields: [schoolId], references: [id], onDelete: Cascade)\n  studentId       String?\n  student         Student?      @relation(fields: [studentId], references: [id], onDelete: SetNull)\n  filiereId       String?\n  filiere         Filiere?      @relation(fields: [filiereId], references: [id], onDelete: SetNull)\n  gradingPeriodId String\n  gradingPeriod   GradingPeriod @relation(fields: [gradingPeriodId], references: [id], onDelete: Cascade)\n  templateId      String?\n  template        PDFTemplate?  @relation(fields: [templateId], references: [id], onDelete: SetNull)\n  html            String // Snapshot HTML complet\n  generalAverage  String // Moyenne g√©n√©rale au moment de la g√©n√©ration\n  createdAt       DateTime      @default(now())\n  updatedAt       DateTime      @updatedAt\n\n  @@index([schoolId])\n  @@index([gradingPeriodId])\n  @@index([studentId])\n  @@index([filiereId])\n  @@map(\"bulletins\")\n}\n\n// ============================================\n// ENUMS\n// ============================================\n\nenum CourseSchedule {\n  DAY // Cours du jour\n  EVENING // Cours du soir\n}\n\nenum GradingSystem {\n  TRIMESTER // Syst√®me trimestriel (lyc√©es)\n  SEMESTER // Syst√®me semestriel (universit√©s)\n}\n\n// ============================================\n// VIE SCOLAIRE - INCIDENTS\n// ============================================\n\n// Mod√®le Incident (retards, oublis de mat√©riel, comportement, etc.)\nmodel Incident {\n  id          String       @id @default(cuid())\n  studentId   String\n  student     Student      @relation(fields: [studentId], references: [id], onDelete: Cascade)\n  type        IncidentType\n  date        DateTime\n  time        String? // Heure de l'incident (ex: \"08h30\")\n  description String?\n\n  // Contexte du cours (op